import axios from 'axios';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ENTPOINT_USER } from './consts/const';
import ErrorMessage from './components/ErrorMessage';
import { INVALID_EMAIL_OR_PASSWORD_MESSAGE } from './consts/const';

const Login = () => {
  const navigate = useNavigate();

  const [loginId, setLoginId] = useState('');
  const [pass, setPass] = useState('');
  const [errorFlag, setErrorFlag] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  let message = '';

  // ユーザ情報を取得する関数
  const getCurrentUser = async (e) => {
    e.preventDefault(); // フォームのデフォルト送信を防止

    try {
      const res = await axios.get(
        `${ENTPOINT_USER}?email=${loginId}?pass=${pass}`
      );
      console.log(res);
      const resData = res.data[0];

      // const res1 = await axios.post(ENTPOINT_USER, {
      //   email: loginId,
      //   pass: pass,
      // });
      // console.log(res1);

      if (resData) {
        authenticateAndRoute(resData);
      } else {
        setErrorMessage(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
        setErrorFlag(true);
      }
    } catch (e) {
      console.error('Login error:', e);
      setErrorFlag(true);
    }

    // try {
    //   const res = await axios.get(`${ENTPOINT_USER}?email=${loginId}`); // 入力したログインIDと一致するデータを取得
    //   const resData = res.data[0]; // レスポンスデータを取得

    //   const res1 = await axios.post(ENTPOINT_USER, {
    //     email: loginId,
    //     pass: pass,
    //   });
    //   console.log(res1);

    //   if (resData) {
    //     authenticateAndRoute(resData);
    //   } else {
    //     setErrorMessage(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
    //     setErrorFlag(true);
    //   }
    // } catch (e) {
    //   console.error('Login error:', e);
    //   setErrorFlag(true);
    // }
  };

  // ユーザ情報を確認し、マイページへ遷移、またはエラーメッセージを関数にする処理
  const authenticateAndRoute = (resData) => {
    if (resData.pass === pass) {
      navigate('/mypage'); // マイページにリダイレクト
    } else {
      setErrorFlag(true);
      setErrorMessage(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
    }
  };

  return (
    <>
      <h1>ログイン</h1>
      <form onSubmit={getCurrentUser} className='signup-form'>
        <input
          type='email'
          placeholder='ログインID（メールアドレス）'
          value={loginId}
          onChange={(e) => {
            setLoginId(e.target.value);
          }}
          className='input-field'
        />
        <input
          type='password'
          placeholder='パスワード'
          value={pass}
          onChange={(e) => {
            setPass(e.target.value);
          }}
          className='input-field'
        />
        <button type='submit' className='submit-button'>
          ログイン
        </button>
      </form>
      {String(errorFlag)}
      {errorFlag && <ErrorMessage message={errorMessage} />}
    </>
  );
};

export default Login;
