import { useState, useEffect } from 'react';
import axios from 'axios';
import SignUpName from './components/SignUpName';
import SignUpBirthday from './components/SignUpBirthday';
import SignUpEmail from './components/SignUpEmail';
import SignUpPass from './components/SignUpPass';
import SignUpCheckPass from './components/SignUpCheckPass';
import ErrorMessage from './components/ErrorMessage';
import {
  ENDPOINT_USER,
  EMAIL_ALREADY_REGISTERED,
  FETCH_DATA_FAILURE,
} from './consts/const';
import { useNavigate } from 'react-router-dom';

/**
 * 新規登録画面
 */
const SignUp = () => {
  // 氏名入力値の状態管理／氏名のエラーフラグ
  const [name, setName] = useState('');
  const [nameErrorFlag, setNameErrorFlag] = useState(true);

  // 生年月日の状態管理／生年月日のエラーフラグ
  const [birthday, setBirthday] = useState('');
  const [birthdayErrorFlag, setbirthdayErrorFlag] = useState(true);

  // メールアドレス入力値の状態管理／メールアドレスのエラーフラグ
  const [email, setEmail] = useState('');
  const [emailErrorFlag, setEmailErrorFlag] = useState(true);

  // パスワード入力値の状態管理／パスワードのエラーフラグ
  const [pass, setPass] = useState('');
  const [passErrorFlag, setPassErrorFlag] = useState(true);

  // 確認パスワード入力値の状態管理／確認パスワードのエラーフラグ
  const [checkPass, setCheckPass] = useState('');
  const [checkPassErrorFlag, setCheckPassErrorFlag] = useState(true);

  // 規約同意チェックボックスの状態管理
  const [isCheck, setIsCheck] = useState(false);

  // 全エラーフラグを一つの条件にまとめたエラーフラグ
  const [isAllErrorFlag, setIsAllErrorFlag] = useState(true);

  // 既に登録中のアドレスであるかのフラグ
  const [isRegisteredEmail, setIsRegisteredEmail] = useState(false);

  // 全エラーフラグを一つの条件でチェックした真偽値
  const allErrorFlags = [
    nameErrorFlag,
    birthdayErrorFlag,
    emailErrorFlag,
    passErrorFlag,
    checkPassErrorFlag,
    !isCheck,
  ];

  /**
   * 全体エラーフラグの更新を行う処理
   *
   * @param {boolean} hasAnyError allErrorFlagsの真偽値を一つにまとめた真偽値。
   * @param {function} setIsAllErrorFlag 全エラーフラグを一つの条件にまとめたエラーフラグの更新関数。
   *
   * @hasAnyError allErrorFlagsの真偽値を格納。
   * @setIsAllErrorFlag hasAnyErrorの真偽値を代入。
   */
  useEffect(() => {
    const hasAnyError = allErrorFlags.some(Boolean);
    setIsAllErrorFlag(hasAnyError);
  }, [
    nameErrorFlag,
    birthdayErrorFlag,
    emailErrorFlag,
    passErrorFlag,
    checkPassErrorFlag,
    isCheck,
  ]);

  const navigate = useNavigate();

  /**
   * ユーザの新規登録を行う関数
   *
   * @param {Event} e（イベント）
   *
   * @param {string} name 氏名のリアクティブな入力値
   * @param {date} birthday 生年月日のリアクティブな入力値
   * @param {string} email アドレスのリアクティブな入力値
   * @param {string} pass パスワードのリアクティブな入力値
   * @param {function} setIsRegisteredEmail 既に登録中のアドレスであるかのフラグの更新関数
   *
   * @param res 指定したemailのユーザ情報をリクエストしたレスポンスデータ。結果を格納。
   * @param registerUser 新規登録するデータ（各入力値が格納）。
   *
   * @res 指定したemailと合致するデータがあるかリクエストした結果を格納。
   * @if文 レスポンスデータ(res.data)が0件であるか確認
   *   true
   *     └@setIsRegisteredEmail falseに更新。
   *     └@registerUser 新規登録するデータを格納。
   *     └@ユーザを新規追加。
   *     └@トップ画面に遷移。
   *   false
   *     └@setIsRegisteredEmail trueに更新。
   *   例外
   *     └@setIsRegisteredEmail trueに更新。
   *     └@コンソールエラーを出力。
   */
  const newRegisterUser = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.get(`${ENDPOINT_USER}?email=${email}`);
      if (res.data.length === 0) {
        setIsRegisteredEmail(false);
        const registerUser = {
          name,
          birthday,
          email,
          pass,
        };
        await axios.post(ENDPOINT_USER, registerUser);
        navigate('/');
      } else {
        setIsRegisteredEmail(true);
      }
    } catch (e) {
      setIsRegisteredEmail(true);
      console.error(FETCH_DATA_FAILURE, e);
    }
  };

  return (
    <>
      <h1>新規登録</h1>
      <form onSubmit={newRegisterUser} className='signup-form'>
        <SignUpName
          nameState={[name, setName]}
          nameErrorFlag={[nameErrorFlag, setNameErrorFlag]}
        />
        <SignUpBirthday
          birthdayState={[birthday, setBirthday]}
          birthdayErrorFlag={[birthdayErrorFlag, setbirthdayErrorFlag]}
        />
        <SignUpEmail
          emailState={[email, setEmail]}
          emailErrorFlag={[emailErrorFlag, setEmailErrorFlag]}
        />
        <SignUpPass
          passState={[pass, setPass]}
          passErrorFlag={[passErrorFlag, setPassErrorFlag]}
        />
        <SignUpCheckPass
          passState={pass}
          checkPassState={[checkPass, setCheckPass]}
          checkPassErrorFlag={[checkPassErrorFlag, setCheckPassErrorFlag]}
        />
        <div className='checkbox-container'>
          <label>
            <input
              type='checkbox'
              checked={isCheck}
              onChange={() => setIsCheck(!isCheck)}
              className='input-checkbox'
            />
            規約同意
          </label>
        </div>
        <button
          type='submit'
          className={`submit-button ${
            isAllErrorFlag ? 'submit-button-disabled' : ''
          }`}
          disabled={isAllErrorFlag}
        >
          登録
        </button>
      </form>
      {isRegisteredEmail && <ErrorMessage message={EMAIL_ALREADY_REGISTERED} />}
    </>
  );
};

export default SignUp;
