import { Link } from 'react-router-dom';

const BlogList = ({ post }) => {
  return (
    <>
      <section class='section'>
        <div class='container'>
          <article>
            <Link
              to={{
                pathname: 'blogDetail',
                state: { post: post },
              }}
              className='listA'
            >
              <div class='text'>
                <p class='h2'>{post.title}</p>
                <p className='ellipsis'>{post.content}</p>
              </div>
            </Link>
          </article>
        </div>
      </section>
    </>
  );
};

export default BlogList;
