import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

// Reduxスライスを生成
const userSlice = createSlice({
  name: 'user',
  initialState: { authUserData: [], authUserBlog: [] },
  reducers: {
    addAuthUserData(state, { payload }) {
      state.authUserData = payload;
    },
    addAuthUserBlog(state, { payload }) {
      console.log(state.authUserBlog);
      state.authUserBlog = payload;
    },
    allClear(state) {
      state.authUserData = [];
      state.authUserBlog = [];
    },
  },
  // ユーザプロフィール取得の非同期通信からstateに格納
  extraReducers(builder) {
    builder.addCase(userAsyncCreator.pending, () => {});
    builder.addCase(userAsyncCreator.fulfilled, () => {});
    builder.addCase(userAsyncCreator.rejected, () => {});
  },
});

// 非同期アクションクリエーター生成
// 第二引数は、非同期通信でユーザプロフィールを取得する処理
export const userAsyncCreator = createAsyncThunk(
  'user/userProfile',
  async () => {}
);

export const { addAuthUserData, addAuthUserBlog, allClear } = userSlice.actions;

export default userSlice.reducer;
