import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './ErrorMessage';
import { PASSWORD_MISMATCH } from '../consts/const';

/**
 * 確認パスワードバリデーションのコンポーネント
 *
 * @param {string, function} passState（パスワード欄の入力値）
 * @param {string, function} passState（確認パスワード欄の入力値）
 * @param {boolean} passErrorFlag （確認パスワードのエラーフラグ）
 * @param {string} placeholder（プレースホルダー）
 */
const CheckPassValidate = ({
  passState,
  checkPassState,
  checkPassErrorFlag,
  placeholder,
}) => {
  const pass = passState; // パスワードの入力値の状態管理（props）
  const [checkPass, setCheckPass] = checkPassState; //  確認パスワードの入力値の状態管理（props）
  const [errorFlag, setErrorFlag] = checkPassErrorFlag; // 確認パスワードの入力値のエラーフラグ（props）
  const [isFirstInputPass, setIsFirstInputPass] = useState(false); // 確認パスワードの初期入力フラグ

  /**
   * 確認パスワードをバリデーションチェックする関数
   *
   * @param {string} pass（パスワードの入力値）
   * @param {string} checkPass（確認パスワードの入力値）
   *
   * @ パスワード入力値と確認パスワード入力値が合致しているか確認
   * @ 一致=> エラーフラグをtrue、不一致=> エラーフラグをfalse
   */
  const validatePassword = useCallback(() => {
    pass === checkPass ? setErrorFlag(false) : setErrorFlag(true);
  }, [pass, checkPass]);

  // 「パスワード」「確認パスワード」「初期入力フラグ」が更新されたとき、バリデーションチェック関数を実行
  useEffect(() => {
    validatePassword();
  }, [pass, checkPass, isFirstInputPass]);

  return (
    <>
      <input
        type='password'
        placeholder={placeholder}
        value={checkPass}
        onChange={(e) => {
          setCheckPass(e.target.value);
        }}
        onBlur={() => setIsFirstInputPass(true)}
      />
      {isFirstInputPass && errorFlag && (
        <ErrorMessage message={PASSWORD_MISMATCH} />
      )}
    </>
  );
};

export default CheckPassValidate;
