import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './ErrorMessage';
import {
  GENERAL_TEXT_VALIDATION_PATTERN,
  NAME_REQUIRED,
  NAME_MAX_LENGTH,
  NAME_INVALID_CHARS,
} from '../consts/const';

/**
 * 氏名欄のコンポーネント
 *
 * @param {string} nameState
 * @param {boolean} nameErrorFlag
 */
const SignUpName = ({ nameState, nameErrorFlag }) => {
  const [name, setName] = nameState; // 氏名入力値の状態管理（props）
  const [errorFlag, setErrorFlag] = nameErrorFlag; // 氏名のエラーフラグ（更新関数のみ）（props）
  const [isFirstInputName, setIsFirstInputName] = useState(false); // 氏名の初期入力フラグ
  const [errorMessage, setErrorMessage] = useState(''); // 氏名のエラーメッセージの状態管理

  /**
   * 氏名欄のバリデーションオブジェクト
   *
   * @ 入力されているか／エラーメッセージ
   * @ 20文字以上であるか／エラーメッセージ
   * @ 許容されていない特殊文字があるか／エラーメッセージ
   */
  const validationNameRules = [
    {
      test: (name) => !name,
      message: NAME_REQUIRED,
    },
    {
      test: (name) => name.length > 20,
      message: NAME_MAX_LENGTH,
    },
    {
      test: (name) => GENERAL_TEXT_VALIDATION_PATTERN.test(name),
      message: NAME_INVALID_CHARS,
    },
  ];

  /**
   * 氏名のバリデーションチェック関数
   *
   * @ rule: nameをvalidationNameRulesオブジェクトの全てのtestメソッドにかけ、その真偽値を格納
   * @ message: rule=> true
   *            └rule.messageの値をエラーメッセージをセット
   * @ エラーメッセージの有無に応じ、エラーフラグを更新
   */
  const validateName = useCallback(() => {
    const rule = validationNameRules.find((rule) => rule.test(name));
    const message = rule ? rule.message : '';
    console.log(rule);
    console.log(message);

    setErrorMessage(message);
    setErrorFlag(Boolean(message));
  }, [name]);

  // 氏名欄が入力されたときに、バリデーションチェック関数を呼び出す
  useEffect(() => {
    validateName();
  }, [name]);

  return (
    <>
      <input
        type='text'
        placeholder='氏名（20文字以内）'
        value={name}
        onChange={(e) => {
          setName(e.target.value);
        }}
        onBlur={() => setIsFirstInputName(true)}
      />
      {isFirstInputName && errorFlag && <ErrorMessage message={errorMessage} />}
    </>
  );
};

export default SignUpName;
