import { useState } from 'react';
import SignUpName from './components/SignUpName';
import SignUpPass from './components/SignUpEmail';

const SignUp = () => {
  // 氏名の状態管理／入力フラグ／エラーフラグ
  const [name, setName] = useState(null);
  const [isErrorName, setIsErrorName] = useState(false);

  // メールアドレスの状態管理／入力フラグ／エラーフラグ
  const [email, setEmail] = useState(null);
  const [isFirstInputEmail, setIsFirstInputEmail] = useState(false);

  // パスワードの状態管理／入力フラグ／エラーフラグ
  const [pass, setPass] = useState(null);
  const [isRevealPassword, setIsRevealPassword] = useState(false);

  const [checkPass, setCheckPass] = useState(null);
  const [ruleCheck, setRuleCheck] = useState(null);
  const [isDisable, setIsDisable] = useState(false);

  return (
    <>
      <h1>新規登録</h1>
      <form onSubmit={''} className='signup-form'>
        <SignUpName
          nameState={[name, setName]}
          setIsErrorName={setIsErrorName}
        />
        <input
          type='date'
          placeholder='生年月日'
          value={'dob'}
          onChange={(e) => {}}
        />
        <SignUpEmail
          emailState={[email, setEmail]}
          setIsFirstInputEmail={setIsFirstInputEmail}
        />
        <input
          type='password'
          placeholder='パスワード'
          value={pass}
          onChange={(e) => {
            setPass(e.target.value);
          }}
        />
        <input
          type='password'
          placeholder='確認パスワード'
          value={''}
          onChange={(e) => {}}
        />
        <div className='checkbox-container'>
          <label>
            <input
              type='checkbox'
              checked={false}
              onChange={(e) => {}}
              className='input-checkbox'
            />
            規約同意
          </label>
        </div>
        <button type='submit' className='submit-button'>
          登録
        </button>
      </form>
    </>
  );
};

export default SignUp;
