import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import axios from 'axios';
import { ENDPOINT_BLOG, ENDPOINT_USER } from './consts/const';
import {
  addBlogPosts as addPostsActions,
  addUserData,
} from './store/modules/userState';
import BlogList from './components/BlogList';

const Mypage = () => {
  const userState = useSelector((state) => state.user.userState);
  const blogPosts = useSelector((state) => state.user.blogPosts);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  console.log(userState);

  // ログイン完了後、userStateのIDをエンコードしてローカルストレージに格納
  if (userState.length !== 0) {
    const encodedUserId = btoa(userState.id);
    localStorage.setItem('userToken', encodedUserId);
  }

  // ローカルストレージから取得
  const storedToken = localStorage.getItem('userToken');
  const decodedUserId = atob(storedToken);

  // ユーザのデータを取得
  useEffect(() => {
    (async () => {
      try {
        const res = await axios.get(`${ENDPOINT_USER}?id=${decodedUserId}`);
        dispatch(addUserData(res.data[0]));
      } catch (e) {
        throw new Error('ネットワークエラーが発生しました。');
      }
    })();
  }, []);

  // ユーザのブログデータを取得
  useEffect(() => {
    (async () => {
      try {
        const res = await axios.get(`${ENDPOINT_BLOG}?keyId=${decodedUserId}`);
        const posts = res.data[0].posts;
        dispatch(addPostsActions(posts));
      } catch (e) {
        console.error('ブログデータの取得に失敗しました', e);
        navigate('/');
      }
    })();
  }, []);

  return (
    <>
      <h1>{}さんのページ</h1>
      <Link to='newPost' className='button new-post-button'>
        新規投稿
      </Link>
      <Link to='changepass' className='button change-pass-button'>
        パスワード変更
      </Link>
      {blogPosts.length ? (
        blogPosts.map((post) => <BlogList post={post} key={post.postDate} />)
      ) : (
        <div className='no-poslts-message'>投稿がありません</div>
      )}
      <button onClick={() => navigate('/')} className='button logout-button'>
        ログアウト
      </button>
    </>
  );
};

export default Mypage;
