import { useEffect, useState } from 'react';
// import { useNavigate } from 'react-router-dom';
import { ENDPOINT_USER, FETCH_DATA_FAILURE } from './consts/const';
import { useDispatch, useSelector } from 'react-redux';
import { addUserData } from './store/modules/userState';
import axios from 'axios';
import ErrorMessage from './components/ErrorMessage';
import PassValidate from './components/PassValidate';

const ChangePassword = () => {
  const [inputPass, setInputPass] = useState(''); // 現在のパスワードの状態管理
  const [inputPassErrorFlag, setInputPassErrorFlag] = useState(''); // 現在のパスワードのエラーフラグ
  const [isFirstInputPass, setIsFirstInputPass] = useState(false); //  現在のパスワードの初期入力フラグ

  const [newPass, setNewPass] = useState(''); // 新しいパスワードの状態管理
  const [checkNewPass, setCheckNewPass] = useState(''); // 新しいパスワード（確認）の状態管理
  const [errorFlag, setErrorFlag] = useState(false); // エラーフラグ
  const [passErrorFlag, setPassErrorFlag] = useState(false); // エラーフラグ（props）

  const userState = useSelector((state) => state.user.userState);

  console.log(isFirstInputPass);

  // 現在のパスワードが初期入力中でない場合、現在のパスワードのバリデーションチェックを行う
  useEffect(() => {
    if (isFirstInputPass) {
      validateInputPass();
    }
  }, [inputPass]);

  // 現在のパスワードのバリデーションチェック関数
  const validateInputPass = () => {
    if (inputPass !== userState.pass) {
      setInputPassErrorFlag(true);
    } else {
      setInputPassErrorFlag(false);
    }

    // navigate('/mypage'); // マイページにリダイレクト
  };

  const a = (e) => {
    e.preventDefault(); // フォームのデフォルト送信を防止
  };

  return (
    <>
      <form onSubmit={a} className='signup-form'>
        <input
          type='password'
          placeholder='現在のパスワード'
          value={inputPass}
          onChange={(e) => {
            setInputPass(e.target.value);
          }}
          onBlur={() => setIsFirstInputPass(true)}
          className='input-field'
        />
        {isFirstInputPass && inputPassErrorFlag && (
          <ErrorMessage message={'現在のパスワードと一致していません'} />
        )}
        <PassValidate
          passState={[newPass, setNewPass]}
          passErrorFlag={[passErrorFlag, setPassErrorFlag]}
          placeholder={'新しいパスワード'}
        />
        <input
          type='password'
          placeholder='新しいパスワード（確認）'
          value={checkNewPass}
          onChange={(e) => {
            setCheckNewPass(e.target.value);
          }}
          className='input-field'
        />
        <button
          type='submit'
          className='submit-button submit-button-disabled'
          disabled={true}
        >
          変更
        </button>
      </form>
    </>
  );
};

export default ChangePassword;
