import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './ErrorMessage';
import { EMAIL_INVALID, EMAIL_VALIDATION_PATTERN } from '../consts/const';

const SignUpEmail = ({ emailState, setEmailErrorFlag }) => {
  const [email, setEmail] = emailState; // 入力値の状態管理（props）
  const setErrorFlag = setEmailErrorFlag; // エラーフラグ_更新関数のみ（props）
  const [isFirstInputEmail, setIsFirstInputEmail] = useState(false); // 初期入力フラグ
  const [errorMessage, setErrorMessage] = useState(null); // エラーメッセージの状態管理

  // メールアドレスのバリデーションチェックを行う関数
  const validateEmail = useCallback(() => {
    const isEmailValid = EMAIL_VALIDATION_PATTERN.test(email); // メールアドレス形式が正しいか
    isEmailValid ? setErrorMessage(null) : setErrorMessage(EMAIL_INVALID); // 正しかったらエラーメッセージに何も代入せず、正しくなかったらエラーメッセージを代入

    setErrorFlag(!!errorMessage); // errorMessageがfalsyな値だったらfalse、truthyな値だったらtrue
  }, [email]);

  // 初期入力時のみ、バリデーションチェックを実行しない
  useEffect(() => {
    if (isFirstInputEmail) {
      validateEmail();
    }
  }, [isFirstInputEmail, validateEmail]);

  return (
    <>
      <input
        type='email'
        placeholder='メールアドレス'
        value={email}
        onChange={(e) => {
          setEmail(e.target.value);
        }}
        onBlur={() => {
          setIsFirstInputEmail(true);
        }}
      />
      {errorMessage && <ErrorMessage message={errorMessage} />}
    </>
  );
};

export default SignUpEmail;
