import { Link } from 'react-router-dom';

const BlogList = ({ post }) => {
  return (
    <>
      <Link to='newPost' className='blog-post'>
        <span className='blog-title'>{post.title}</span>
        <span className='blog-content'>{post.content}</span>
      </Link>
    </>
  );
};

export default BlogList;
