import axios from 'axios';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { addAuthUserProfile as addAuthUserProfileActions } from './store/modules/userState';

import {
  ENDPOINT_USER,
  INVALID_EMAIL_OR_PASSWORD_MESSAGE,
} from './consts/const';
import ErrorMessage from './components/ErrorMessage';

/**
 * ログイン画面
 */
const Login = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [loginId, setLoginId] = useState(''); // ログインID（アドレス）入力値の状態管理
  const [pass, setPass] = useState(''); // パスワードの入力値の状態管理
  const [errorFlag, setErrorFlag] = useState(false); // ログインのエラーフラグの状態管理
  const [errorMessage, setErrorMessage] = useState(''); // エラーメッセージの状態管理

  // アドレスとパスワードの入力値をチェックする関数

  /**
   * アドレスとパスワードの入力値をチェックする関数
   *
   * @param {Event} e
   * @ isInputEmpty: 引数の値の空白を除去
   * @ アドレス、またはパスワードが空文字か確認
   *     └settingError: エラーメッセージを更新
   * @ handleUserLogin関数を呼び出し
   */
  const velidateEmailAndPass = (e) => {
    e.preventDefault(); // フォームのデフォルト送信を防止

    const isInputEmpty = (input) => input.trim() === '';
    if (isInputEmpty(loginId) || isInputEmpty(pass)) {
      settingError(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
      return;
    }

    handleUserLogin();
  };

  /**
   * ユーザプロフィールの取得状況に応じた処理を行う関数
   *
   * @ resData: ユーザ情報を取得する関数の戻りと
   */
  const handleUserLogin = async () => {
    try {
      const resData = await getUserData();
      if (resData.length) {
        dispatch(addAuthUserProfileActions(resData[0])); // レスポンスデータをstoreに格納
        navigate('/mypage'); // マイページに遷移
      } else {
        settingError(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
      }
    } catch (e) {
      settingError(e.message);
    }
  };

  // ユーザ情報を取得する関数
  const getUserData = async (e) => {
    try {
      const res = await axios.get(
        `${ENDPOINT_USER}?email=${loginId}&pass=${pass}`
      );
      return res.data;
    } catch (e) {
      throw new Error('ネットワークエラーが発生しました。');
    }
  };

  // エラーをセットする関数
  const settingError = (message) => {
    setErrorFlag(true);
    setErrorMessage(message);
  };

  return (
    <>
      <h1>ログイン</h1>
      <form onSubmit={velidateEmailAndPass} className='signup-form'>
        <input
          type='email'
          placeholder='ログインID（メールアドレス）'
          value={loginId}
          onChange={(e) => {
            setLoginId(e.target.value);
          }}
          className='input-field'
        />
        <input
          type='password'
          placeholder='パスワード'
          value={pass}
          onChange={(e) => {
            setPass(e.target.value);
          }}
          className='input-field'
        />
        <button type='submit' className='submit-button'>
          ログイン
        </button>
      </form>
      {errorFlag && <ErrorMessage message={errorMessage} />}
    </>
  );
};

export default Login;
