import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './ErrorMessage';
import {
  GENERAL_TEXT_VALIDATION_PATTERN,
  NAME_REQUIRED,
  NAME_MAX_LENGTH,
  NAME_INVALID_CHARS,
} from '../consts/const';

const SignUpBirthday = ({ birthdayState, setbirthdayErrorFlag }) => {
  const [birthday, setBirthday] = birthdayState; // 入力値の状態管理（props）
  const setErrorFlag = setbirthdayErrorFlag; // エラーフラグ_更新関数のみ（props）
  const [isFirstInputBirthday, setIsFirstInputBirthday] = useState(false); // 初期入力フラグ
  const [errorMessage, setErrorMessage] = useState(null); // エラーメッセージの状態管理

  // 生年月日のバリデーションチェックを行う関数
  const validateBirthday = useCallback(() => {}, [birthday]);

  // 初期入力時のみ、バリデーションチェックを実行しない
  useEffect(() => {
    if (isFirstInputBirthday) {
      validateBirthday();
    }
  }, []);

  return (
    <>
      <input
        type='date'
        placeholder='生年月日'
        value={birthday}
        onChange={(e) => {
          setBirthday(e.target.value);
        }}
      />
    </>
  );
};

export default SignUpBirthday;
