const SignUp = () => {
  return (
    <form onSubmit={handleSubmit}>
      <Input
        type='text'
        placeholder='氏名'
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <Input
        type='date'
        placeholder='生年月日'
        value={dob}
        onChange={(e) => setDob(e.target.value)}
      />
      <Input
        type='email'
        placeholder='メールアドレス'
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <Input
        type='password'
        placeholder='パスワード'
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <CheckBoxContainer>
        <input
          type='checkbox'
          checked={agree}
          onChange={(e) => setAgree(e.target.checked)}
        />
        規約同意
      </CheckBoxContainer>
      <Button type='submit'>登録</Button>
    </form>
  );
};

export default SignUp;
