import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import axios from 'axios';
import { ENDPOINT_BLOG } from './consts/const';
import { addBlogPosts as addPostsActions } from './store/modules/userState';
import BlogList from './components/BlogList';

const Mypage = () => {
  const userState = useSelector((state) => state.user.userState);
  const blogPosts = useSelector((state) => state.user.blogPosts);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  // ログイン完了後、userStateのIDをlocalStorageに格納
  if (userState.length !== 0) {
    localStorage.setItem('userId', userState.id);
  }

  const userId = localStorage.getItem('userId'); // localStorageからuserIdを取得
  console.log(userId);

  if (!userId) {
    navigate('/'); // userIdがない場合はリダイレクト
  }

  // ユーザのブログデータを取得
  useEffect(() => {
    (async () => {
      try {
        const res = await axios.get(`${ENDPOINT_BLOG}?keyId=${userId}`);
        const posts = res.data[0].posts;
        dispatch(addPostsActions(posts));
      } catch (e) {
        console.error('ブログデータの取得に失敗しました', e);
        // navigate('/');
      }
    })();
  }, []);

  return (
    <>
      <h1>{userState.name}さんのページ</h1>
      <Link to='newPost' className='button new-post-button'>
        新規投稿
      </Link>
      <Link to='changepass' className='button change-pass-button'>
        パスワード変更
      </Link>
      {blogPosts.length ? (
        blogPosts.map((post) => <BlogList post={post} key={post.postDate} />)
      ) : (
        <div className='no-posts-message'>投稿がありません</div>
      )}
    </>
  );
};

export default Mypage;
