import axios from 'axios';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  ENTPOINT_USER,
  INVALID_EMAIL_OR_PASSWORD_MESSAGE,
  NETWORK_ERROR,
} from './consts/const';
import ErrorMessage from './components/ErrorMessage';

const Login = () => {
  const navigate = useNavigate();

  const [loginId, setLoginId] = useState(''); // ログインID入力値の状態管理
  const [pass, setPass] = useState(''); // パスワード入力値の状態管理
  const [errorFlag, setErrorFlag] = useState(false); // エラーフラグの状態管理
  const [errorMessage, setErrorMessage] = useState(''); // エラーメッセージの状態管理

  // ユーザ情報を取得して、ページ遷移またはエラーメッセージを表示する関数
  const getCurrentUser = async (e) => {
    e.preventDefault(); // フォームのデフォルト送信を防止

    try {
      const res = await axios.get(
        `${ENTPOINT_USER}?email=${loginId}&pass=${pass}`
      );

      if (res.data.length) {
        navigate('/mypage');
      } else {
        setErrorFlag(true);
        setErrorMessage(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
      }
    } catch (e) {
      setErrorFlag(true);
      setErrorMessage(NETWORK_ERROR);
    }
  };

  // // ユーザ情報を確認し、マイページへ遷移、またはエラーメッセージを関数にする処理
  // const authenticateAndRoute = (resData) => {
  //   setErrorFlag(true);
  //   setErrorMessage(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
  // };

  return (
    <>
      <h1>ログイン</h1>
      <form onSubmit={getCurrentUser} className='signup-form'>
        <input
          type='email'
          placeholder='ログインID（メールアドレス）'
          value={loginId}
          onChange={(e) => {
            setLoginId(e.target.value);
          }}
          className='input-field'
        />
        <input
          type='password'
          placeholder='パスワード'
          value={pass}
          onChange={(e) => {
            setPass(e.target.value);
          }}
          className='input-field'
        />
        <button type='submit' className='submit-button'>
          ログイン
        </button>
      </form>
      {errorFlag && <ErrorMessage message={errorMessage} />}
    </>
  );
};

export default Login;
