import { useCallback, useEffect, useState } from 'react';
import {
  UPPERCASE_REGEX,
  LOWERCASE_REGEX,
  DIGIT_REGEX,
  SPECIAL_CHAR_REGEX,
  PASSWORD_CRITERIA_ERROR_MESSAGE,
} from '../consts/const';
import ErrorMessage from './ErrorMessage';

const SignUpPass = ({ passState, passErrorFlag }) => {
  const [pass, setPass] = passState; // 入力値の状態管理（props）
  const [errorFlag, setErrorFlag] = passErrorFlag; // エラーフラグ_更新関数のみ（props）
  const [isFirstInputPass, setIsFirstInputPass] = useState(false); // 初期入力フラグ

  // パスワードのバリデーション状態を一つのオブジェクトで管理
  const [validation, setValidation] = useState({
    isLengthValid: false, // 10文字以上入力されているか状態管理
    hasUppercase: false, // 大文字が含まれているか状態管理
    hasLowercase: false, // 小文字が含まれているか状態管理
    hasDegit: false, // 数字が含まれているか状態管理
    hasSpecialChar: false,
  });

  const validatePassword = useCallback(() => {
    const newValidation = {
      isLengthValid: String(pass).length >= 10,
      hasUppercase: UPPERCASE_REGEX.test(pass),
      hasLowercase: pass ? LOWERCASE_REGEX.test(pass) : false,
      hasDegit: DIGIT_REGEX.test(pass),
      hasSpecialChar: SPECIAL_CHAR_REGEX.test(pass),
    };

    setValidation(newValidation); // 一度の更新で全てのバリデーション状態を設定

    const isCriteriaMet = Object.values(newValidation).every(Boolean);
    setErrorFlag(isCriteriaMet); // 全ての条件を満たしているかのフラグを設定
  }, [pass]);

  // パスワードの入力条件を満たしているか確認
  useEffect(() => {
    validatePassword();
  }, [pass]);

  return (
    <>
      <input
        type='password'
        placeholder='パスワード'
        value={pass}
        onChange={(e) => {
          setPass(e.target.value);
        }}
        onBlur={() => setIsFirstInputPass(true)}
      />
      {isFirstInputPass && !errorFlag && (
        <ErrorMessage message={PASSWORD_CRITERIA_ERROR_MESSAGE} />
      )}
      <ul className='list-container'>
        <li className={`lengthValid-${validation.isLengthValid}`}>
          10文字以上
        </li>
        <li className={`uppercase-${validation.hasUppercase}`}>半角大文字</li>
        <li className={`lowercase-${validation.hasLowercase}`}>半角小文字</li>
        <li className={`degit-${validation.hasDegit}`}>半角数値</li>
        <li className={`special-char-${validation.hasSpecialChar}`}>
          半角記号
        </li>
      </ul>
    </>
  );
};

export default SignUpPass;
