import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './ErrorMessage';
import {
  GENERAL_TEXT_VALIDATION_PATTERN,
  NAME_REQUIRED,
  NAME_MAX_LENGTH,
  NAME_INVALID_CHARS,
} from '../consts/const';

const SignUpName = ({ nameState, errorName }) => {
  const [name, setName] = nameState;
  const [isFirstInputName, setIsFirstInputName] = useState(false); // 初回入力フラグ
  const [errorMessage, setErrorMessage] = useState(null); // エラーメッセージの状態管理

  // 氏名のバリデーションチェックを行う関数
  const validateName = useCallback(() => {
    let message = '';
    if (!name) {
      message = NAME_REQUIRED;
    } else if (name.length > 20) {
      message = NAME_MAX_LENGTH;
    } else if (GENERAL_TEXT_VALIDATION_PATTERN.test(name)) {
      message = NAME_INVALID_CHARS;
    }

    setErrorMessage(message); // エラーメッセージをセット
  }, [name]);

  // 初期入力時のみ、バリデーションを実行しない
  useEffect(() => {
    console.log(isFirstInputName);
    if (isFirstInputName) {
      validateName();
    }
  }, [validateName, isFirstInputName]);

  return (
    <>
      <input
        type='text'
        placeholder='氏名（20文字以内）'
        value={name}
        onChange={(e) => {
          setName(e.target.value);
        }}
        onBlur={() => setIsFirstInputName(true)}
      />
      {errorMessage && <ErrorMessage message={errorMessage} />}
    </>
  );
};

export default SignUpName;
