import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

import ChangeInputPassword from './components/ChangeInputPassword';
import ChangeNewPassword from './ChangeNewPassword';
import ChangeCheckNewPassword from './ChangeCheckNewPassword';
import axios from 'axios';
import { ENDPOINT_USER } from './consts/const';

const ChangePassword = () => {
  const navigate = useNavigate();
  const userState = useSelector((state) => state.user.userState);

  const [inputPass, setInputPass] = useState(''); // 現在のパスワードの状態管理
  const [inputPassErrorFlag, setInputPassErrorFlag] = useState(true); // 現在のパスワードのエラーフラグ

  const [newPass, setNewPass] = useState(''); // 新しいパスワードの状態管理
  const [newPassErrorFlag, setNewPassErrorFlag] = useState(true); //  新しいパスワードのエラーフラグ（props）

  const [checkNewPass, setCheckNewPass] = useState(''); // 確認パスワードの状態管理
  const [newCheckPassErrorFlag, setNewCCheckPassErrorFlag] = useState(true); // 確認パスワードのエラーフラグ（props）

  const [disabledFlag, setDisabledFlag] = useState(true); // 変更ボタンの活性フラグ

  // フラグが更新されたとき、エラーフラグの状態をチェックし、変更ボタンの活性フラグを切り替える
  useEffect(() => {
    if (!inputPassErrorFlag && !newPassErrorFlag && !newCheckPassErrorFlag) {
      setDisabledFlag(false);
    } else {
      setDisabledFlag(true);
    }
  }, [inputPassErrorFlag, newPassErrorFlag, newCheckPassErrorFlag]);

  // パスワードを変更してマイページに遷移する
  const submit = () => {
    changeUserPass();
    navigate('/mypage');
  };

  // ユーザのパスワードを更新する関数
  const changeUserPass = async () => {
    const userNewPass = { ...userState, pass: newPass };
    try {
      const res = await axios.put(
        ENDPOINT_USER + '/' + userState.id,
        userNewPass
      );
    } catch (e) {
      console.error(FETCH_BLOG_DATA_FAILURE, e);
    }
  };

  return (
    <>
      <form onSubmit={submit} className='signup-form'>
        <ChangeInputPassword
          inputPassState={[inputPass, setInputPass]}
          inputPassErrorFlag={[inputPassErrorFlag, setInputPassErrorFlag]}
        />
        <ChangeNewPassword
          newPassState={[newPass, setNewPass]}
          newPassErrorFlag={[newPassErrorFlag, setNewPassErrorFlag]}
          placeholder={'新しいパスワード'}
        />
        <ChangeCheckNewPassword
          passState={newPass}
          checkPassState={[checkNewPass, setCheckNewPass]}
          checkPassErrorFlag={[
            newCheckPassErrorFlag,
            setNewCCheckPassErrorFlag,
          ]}
          placeholder={'新しいパスワード（確認）'}
        />
        <button
          type='submit'
          className={`submit-button submit-button-disabled-${disabledFlag}`}
          disabled={disabledFlag}
        >
          変更
        </button>
      </form>
    </>
  );
};

export default ChangePassword;
