import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import axios from 'axios';
import { ENDPOINT_BLOG } from './consts/const';
import { addBlogPosts as addPostsActions } from './store/modules/userState';

const Mypage = () => {
  const userState = useSelector((state) => state.user.userState);
  const blogPosts = useSelector((state) => state.user.blogPosts);
  const dispatch = useDispatch();

  console.log(blogPosts);

  // ユーザのブログデータを取得
  useEffect(() => {
    (async () => {
      try {
        const res = await axios.get(`${ENDPOINT_BLOG}?keyId=${userState.id}`);
        console.log(res.data[0].posts);
        const posts = res.data[0].posts;
        dispatch(addPostsActions(posts));
      } catch (e) {
        console.error('ブログデータの取得に失敗しました', e);
      }
    })();
  }, []);

  return (
    <>
      <h1>{userState.name}様のページ</h1>
      <Link to='changepass' className='button change-pass-button'>
        パスワード変更
      </Link>
      {blogPosts.map((post) => (
        <div>{post.title}</div>
      ))}
    </>
  );
};

export default Mypage;
