import { useCallback, useEffect } from 'react';
import { GENERAL_TEXT_VALIDATION_PATTERN } from '../consts/const';
import ErrorMessage from './ErrorMessage';

const SignUpPass = ({ emailState, firstInputEmail, errorName }) => {
  const [email, setEmail] = emailState;
  const [isFirstInputEmail, setIsFirstInputEmail] = firstInputEmail;
  const [isErrorName, setIsErrorName] = errorName;

  // 氏名のバリデーションチェックを行う関数
  const validateName = useCallback(() => {
    const isValidateName =
      !name || name.length > 20 || GENERAL_TEXT_VALIDATION_PATTERN.test(name); // 入力が0文字、21文字以上、特殊文字に該当するか

    setIsErrorName(isValidateName); // チェック結果に応じた値を代入
  }, [name]);

  // 初回入力中のみ、氏名バリデーションを実行しない
  useEffect(() => {
    if (isFirstInputName) {
      validateName();
    }
  }, [isFirstInputName, validateName]);

  return (
    <>
      <input
        type='email'
        placeholder='メールアドレス'
        value={email}
        onChange={(e) => {
          setEmail(e.target.value);
        }}
        onBlur={() => {
          setIsFirstInputEmail(true);
        }}
      />
    </>
  );
};

export default SignUpPass;
