import { Link } from 'react-router-dom';

const BlogList = ({ post }) => {
  return (
    <>
      <section class='listA'>
        <div class='container'>
          <article>
            <Link to='newPost' className='listA'>
              <div
                class='photo'
                style='background-image: url(https://teamsata.com/img/IMG_9438.jpg);'
              ></div>
              <div class='text'>
                <p class='h2'>{post.title}</p>
                <p>{post.content}</p>
              </div>
            </Link>
          </article>
        </div>
      </section>
    </>
  );
};

export default BlogList;
