import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './ErrorMessage';
import {
  GENERAL_TEXT_VALIDATION_PATTERN,
  NAME_REQUIRED,
  NAME_MAX_LENGTH,
  NAME_INVALID_CHARS,
} from '../consts/const';

const SignUpName = ({ nameState, nameErrorFlag }) => {
  const [name, setName] = nameState; // 入力値の状態管理（props）
  const [errorFlag, setErrorFlag] = nameErrorFlag; // エラーフラグ_更新関数のみ（props）
  const [isFirstInputName, setIsFirstInputName] = useState(false); // 初期入力フラグ
  const [errorMessage, setErrorMessage] = useState(null); // エラーメッセージの状態管理

  // 氏名のバリデーションチェックを行う関数
  const validateName = useCallback(() => {
    const rule = validationRules.find((rule) => rule.test(name));
    const message = rule ? rule.message : '';

    setErrorMessage(message);
    setErrorFlag(Boolean(message));
  }, [name]);

  // エラーの条件とエラーメッセージの一覧
  const validationRules = [
    {
      // 氏名欄が入力されていない場合
      test: (name) => !name,
      message: NAME_REQUIRED,
    },
    {
      // 氏名欄が20文字以上の場合
      test: (name) => name.length > 20,
      message: NAME_MAX_LENGTH,
    },
    {
      // 氏名欄に許容されていない特殊文字が使用されている場合
      test: (name) => GENERAL_TEXT_VALIDATION_PATTERN.test(name),
      message: NAME_INVALID_CHARS,
    },
  ];

  // 氏名入力時にバリデーションチェック関数を呼び出す
  useEffect(() => {
    validateName();
  }, [name]);

  return (
    <>
      <input
        type='text'
        placeholder='氏名（20文字以内）'
        value={name}
        onChange={(e) => {
          setName(e.target.value);
        }}
        onBlur={() => setIsFirstInputName(true)}
      />
      {isFirstInputName && errorFlag && <ErrorMessage message={errorMessage} />}
    </>
  );
};

export default SignUpName;
