import { Link } from 'react-router-dom';

const BlogList = ({ post }) => {
  return (
    <>
      <div class='container'>
        <article>
          <Link to='newPost' className='listA'>
            <div class='text'>
              <p class='h2'>{post.title}</p>
              <p className='ellipsis'>{post.content}</p>
            </div>
          </Link>
        </article>
      </div>
    </>
  );
};

export default BlogList;
