import { useCallback, useEffect, useState } from 'react';
import { PASSWORD_MISMATCH } from '../consts/const';
import ErrorMessage from './ErrorMessage';

const SignUpCheckPass = ({ passState, checkPassState, checkPassErrorFlag }) => {
  const pass = passState; // パスワードの状態（props）
  const [checkPass, setCheckPass] = checkPassState; // 入力値の状態管理（props）
  const [errorFlag, setErrorFlag] = checkPassErrorFlag; // エラーフラグ（props）
  const [isFirstInputPass, setIsFirstInputPass] = useState(false); // 初期入力フラグ

  // 確認パスワード入力値のバリデーションチェックを行う関数
  const validatePassword = useCallback(() => {
    if (isFirstInputPass) {
      pass === checkPass ? setErrorFlag(false) : setErrorFlag(true);
    }
  }, [pass, checkPass, errorFlag]);

  // 初期入力時のみ、バリデーションチェックを実行しない
  useEffect(() => {
    // if (isFirstInputPass) {
    validatePassword();
    // }
  }, [checkPass, isFirstInputPass]);

  return (
    <>
      <input
        type='password'
        placeholder='確認パスワード'
        value={checkPass}
        onChange={(e) => {
          setCheckPass(e.target.value);
        }}
        onBlur={() => setIsFirstInputPass(true)}
      />
      {isFirstInputPass && errorFlag && (
        <ErrorMessage message={PASSWORD_MISMATCH} />
      )}
    </>
  );
};

export default SignUpCheckPass;
