import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './ErrorMessage';
import { BIRTHDATE_REQUIRED } from '../consts/const';

const SignUpBirthday = ({ birthdayState, birthdayErrorFlag }) => {
  const [birthday, setBirthday] = birthdayState; // 入力値の状態管理（props）
  const [emailErrorFlag, setEmailErrorFlag] = birthdayErrorFlag; // エラーフラグ_更新関数のみ（props）
  const [isFirstInputBirthday, setIsFirstInputBirthday] = useState(false); // 初期入力フラグ

  // 生年月日のバリデーションチェックを行う関数
  const validateBirthday = useCallback(() => {
    birthday ? setEmailErrorFlag(false) : setEmailErrorFlag(true);
  }, [birthday]);

  // 初期入力時のみ、バリデーションチェックを実行しない
  useEffect(() => {
    if (isFirstInputBirthday) {
      birthday ? setEmailErrorFlag(false) : setEmailErrorFlag(true);
    }
  }, [birthday, isFirstInputBirthday]);

  return (
    <>
      <input
        type='date'
        placeholder='生年月日'
        value={birthday}
        onChange={(e) => {
          setBirthday(e.target.value);
        }}
        onBlur={() => setIsFirstInputBirthday(true)}
      />
      {emailErrorFlag && <ErrorMessage message={BIRTHDATE_REQUIRED} />}
    </>
  );
};

export default SignUpBirthday;
