import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const NewPost = () => {
  const navigate = useNavigate();

  const [title, setTitle] = useState(''); // タイトルの入力値の状態管理
  const [titleErrorFlag, setTitleErrorFlag] = useState(false); // タイトルのエラーフラグ

  const [content, setContent] = useState(''); // 文章の入力値の状態管理
  const [contentErrorFlag, setContentErrorFlag] = useState(false); // 文章のエラーフラグ

  const [disabledFlag, setDisabledFlag] = useState(true); // 投稿ボタンの活性フラグ

  // タイトルと文章の文字数に応じて、投稿ボタンの活性／非活性を制御
  useEffect(() => {
    // タイトルまたは内容が空の場合は、投稿ボタンを無効にする
    const isDisabled = !title || !content;
    setDisabledFlag(isDisabled);

    // タイトルの文字数が30を超えている場合は、エラーフラグを立てる
    const titleTooLong = title.length > 30;
    setTitleErrorFlag(titleTooLong);

    // 内容の文字数が500を超えている場合は、エラーフラグを立てる
    const contentTooLong = content.length > 500;
    setContentErrorFlag(contentTooLong);
  }, [title, content]);

  // ブログを投稿する関数
  const postSubmit = () => {};

  return (
    <>
      <form onSubmit={postSubmit} className=''>
        <div className='new-post-title-area'>
          <textarea
            className='new-post-title-textbox'
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <p className='new-post-title-length'>{`${title.length}文字 / 30文字`}</p>
        </div>
        <div
          className={`new-post-content-area new-post-content-area-${titleErrorFlag}`}
        >
          <textarea
            className='new-post-content-textbox'
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
          <p className='new-post-content-length'>{`${content.length}文字 / 500文字`}</p>
        </div>
        <button
          type='submit'
          className={`submit-button submit-button-disabled-${disabledFlag} new-post-submit-button`}
          disabled={disabledFlag}
        >
          投稿
        </button>
      </form>
    </>
  );
};

export default NewPost;
