import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './ErrorMessage';
import { EMAIL_INVALID, EMAIL_VALIDATION_PATTERN } from '../consts/const';

const SignUpEmail = ({ emailState, setIsErrorEmail }) => {
  const [email, setEmail] = emailState;
  const setErrorEmail = setIsErrorEmail;
  const [isFirstInputEmail, setIsFirstInputEmail] = useState(false);

  // メールアドレスのバリデーションチェックを行う関数
  const validateEmail = useCallback(() => {
    const isEmailValid = EMAIL_VALIDATION_PATTERN.test(email); // メールアドレス形式が正しいか

    setErrorEmail(!isEmailValid); // バリデーション結果に応じて真偽値を代入
  }, [email]);

  // 初期入力時のみ、バリデーションを実行しない
  useEffect(() => {
    if (isFirstInputEmail) {
      validateEmail();
    }
  }, [isFirstInputEmail, validateEmail]);

  return (
    <>
      <input
        type='email'
        placeholder='メールアドレス'
        value={email}
        onChange={(e) => {
          setEmail(e.target.value);
        }}
        onBlur={() => {
          setIsFirstInputEmail(true);
        }}
      />
      {isErrorEmail && <ErrorMessage message={EMAIL_INVALID} />}
    </>
  );
};

export default SignUpEmail;
