import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './ErrorMessage';
import { EMAIL_INVALID, EMAIL_VALIDATION_PATTERN } from '../consts/const';

/**
 * メールアドレスのコンポーネント
 *
 * @param {string, function} emailState（アドレスの入力値／更新関数）
 * @param {boolean, function} emailErrorFlag（アドレスのエラーフラグ／更新関数）
 */
const SignUpEmail = ({ emailState, emailErrorFlag }) => {
  const [email, setEmail] = emailState; //アドレスの入力値の状態管理（props）
  const [errorFlag, setErrorFlag] = emailErrorFlag; // アドレスのエラーフラグ（props）
  const [isFirstInputEmail, setIsFirstInputEmail] = useState(false); // アドレスの初期入力フラグ

  /**
   * アドレスのバリデーションチェック関数
   *
   * @param {string} email（アドレスの入力値）
   *
   * @param isEmailValid 正しいアドレス形式であるかの真偽値
   * @param {function} setErrorFlag アドレスのエラーフラグの更新関数。正しいアドレス形式だったらfalse、正しくない場合はtrueをセット。
   */
  const validateEmail = useCallback(() => {
    const isEmailValid = EMAIL_VALIDATION_PATTERN.test(email);

    setErrorFlag(!isEmailValid);
  }, [email]);

  // アドレスの入力値が更新されたとき、バリデーションチェック関数を呼び出す処理
  useEffect(() => {
    validateEmail();
  }, [email]);

  return (
    <>
      <input
        type='email'
        placeholder='メールアドレス'
        value={email}
        onChange={(e) => {
          setEmail(e.target.value);
        }}
        onBlur={() => {
          setIsFirstInputEmail(true);
        }}
      />
      {isFirstInputEmail && errorFlag && (
        <ErrorMessage message={EMAIL_INVALID} />
      )}
    </>
  );
};

export default SignUpEmail;
