import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const NewPost = () => {
  const navigate = useNavigate();

  const [title, setTitle] = useState(''); // タイトルの入力値の状態管理
  const [titleErrorFlag, setTitleErrorFlag] = useState(false); // タイトルエラーフラグ

  // パスワードをチェック
  const validatePass = (e) => {
    e.preventDefault(); // フォームのデフォルト送信を防止
    navigate('/mypage'); // マイページにリダイレクト
  };

  return (
    <>
      <form onSubmit={validatePass} className=''>
        <textarea className='new-post-title-textbox' />
        {}
        <textarea className='new-post-textbox' />
        <button type='submit' className='submit-button new-post-submit-button'>
          投稿
        </button>
      </form>
    </>
  );
};

export default NewPost;
