import { useCallback, useEffect, useState } from 'react';
import {
  UPPERCASE_REGEX,
  LOWERCASE_REGEX,
  DIGIT_REGEX,
  SPECIAL_CHAR_REGEX,
  PASSWORD_CRITERIA_ERROR_MESSAGE,
} from '../consts/const';
import ErrorMessage from './ErrorMessage';
import PassValidate from './PassValidate';

const SignUpPass = ({ passState, passErrorFlag }) => {
  const [pass, setPass] = passState; // 入力値の状態管理（props）
  const [errorFlag, setErrorFlag] = passErrorFlag; // エラーフラグ（props）
  const [isFirstInputPass, setIsFirstInputPass] = useState(false); // 初期入力フラグ

  // パスワードのバリデーション状態管理
  const [validation, setValidation] = useState({
    isLengthValid: false, // 10文字以上
    hasUppercase: false, // 大文字
    hasLowercase: false, // 小文字
    hasDegit: false, // 数字
    hasSpecialChar: false, // 記号
  });

  // パスワード入力値のバリデーションチェックを行う関数
  const validatePassword = useCallback(() => {
    const newValidation = {
      isLengthValid: String(pass).length >= 10,
      hasUppercase: UPPERCASE_REGEX.test(pass),
      hasLowercase: pass ? LOWERCASE_REGEX.test(pass) : false,
      hasDegit: DIGIT_REGEX.test(pass),
      hasSpecialChar: SPECIAL_CHAR_REGEX.test(pass),
    };
    setValidation(newValidation); // 各入力条件の結果を更新

    const isPasswordFullyValidated =
      Object.values(newValidation).every(Boolean); // パスワード入力条件をすべて満たしているかの真偽値
    setErrorFlag(!isPasswordFullyValidated); // すべて満たしていたらエラーフラグをfalse、そうでなかったらtrue
  }, [pass]);

  // パスワードが更新されたとき、バリデーションチェック関数を呼び出す
  useEffect(() => {
    validatePassword();
  }, [pass]);

  return (
    <>
      <input
        type='password'
        placeholder='パスワード'
        value={pass}
        onChange={(e) => {
          setPass(e.target.value);
        }}
        onBlur={() => setIsFirstInputPass(true)}
      />
      {isFirstInputPass && errorFlag && (
        <ErrorMessage message={PASSWORD_CRITERIA_ERROR_MESSAGE} />
      )}
      <PassValidate />
    </>
  );
};

export default SignUpPass;
