import { useEffect, useState } from 'react';
// import { useNavigate } from 'react-router-dom';
import { ENDPOINT_USER, FETCH_DATA_FAILURE } from './consts/const';
import { useDispatch, useSelector } from 'react-redux';
import { addUserData } from './store/modules/userState';
import axios from 'axios';
import ErrorMessage from './components/ErrorMessage';

const ChangePassword = () => {
  const [inputPass, setInputPass] = useState(''); // 現在のパスワードの状態管理
  const [newPass, setNewPass] = useState(''); // 新しいパスワードの状態管理
  const [checkNewPass, setCheckNewPass] = useState(''); // 新しいパスワード（確認）の状態管理
  const [errorFlag, setErrorFlag] = useState(false); // エラーフラグ（props）

  // パスワードのバリデーション状態管理
  const [validation, setValidation] = useState({
    isLengthValid: false, // 10文字以上
    hasUppercase: false, // 大文字
    hasLowercase: false, // 小文字
    hasDegit: false, // 数字
    hasSpecialChar: false, // 記号
  });

  // const navigate = useNavigate();
  const dispatch = useDispatch();
  const userState = useSelector((state) => state.user.userState);

  // ユーザの情報を取得
  useEffect(() => {
    (async () => {
      try {
        const res = await axios.get(`${ENDPOINT_USER}?id=${decodedUserId}`);
        dispatch(addUserData(res.data[0]));
      } catch (e) {
        throw new Error(FETCH_DATA_FAILURE, e);
      }
    })();
  }, []);

  // ローカルストレージから取得（※今回は仮トークン扱い）
  const storedToken = localStorage.getItem('userToken');
  const decodedUserId = atob(storedToken);

  // 現在のパスワードをチェック
  const validatePass = (e) => {
    e.preventDefault(); // フォームのデフォルト送信を防止

    // 新しいパスワードと新しいパスワード(確認)が合致しているか確認
    if (inputPass !== userState.pass) {
      setErrorFlag(true);
    }

    // navigate('/mypage'); // マイページにリダイレクト
  };

  return (
    <>
      <form onSubmit={validatePass} className='signup-form'>
        <input
          type='password'
          placeholder='現在のパスワード'
          value={inputPass}
          onChange={(e) => {
            setInputPass(e.target.value);
          }}
          className='input-field'
        />

        <input
          type='password'
          placeholder='新しいパスワード'
          value={newPass}
          onChange={(e) => {
            setNewPass(e.target.value);
          }}
          className='input-field'
        />
        <input
          type='password'
          placeholder='新しいパスワード（確認）'
          value={checkNewPass}
          onChange={(e) => {
            setCheckNewPass(e.target.value);
          }}
          className='input-field'
        />
        <button type='submit' className='submit-button'>
          変更
        </button>
      </form>
      {errorFlag && (
        <ErrorMessage message={'現在のパスワードと一致していません'} />
      )}
    </>
  );
};

export default ChangePassword;
