import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './ErrorMessage';
import {
  GENERAL_TEXT_VALIDATION_PATTERN,
  NAME_REQUIRED,
  NAME_MAX_LENGTH,
  NAME_INVALID_CHARS,
} from '../consts/const';

// const SignUpName = ({ nameState, firstInputName, errorName }) => {
const SignUpName = ({ errorName }) => {
  // const [name, setName] = nameState;
  // const [isFirstInputName, setIsFirstInputName] = firstInputName;
  const [name, setName] = useState(null);
  const [isFirstInputName, setIsFirstInputName] = useState(false);
  const [isErrorName, setIsErrorName] = errorName;

  // エラーメッセージの表示フラグ
  const [errorMessage, setErrorMessage] = useState(null);

  // 氏名のバリデーションチェックを行う関数
  const validateName = useCallback(() => {
    let message = '';
    if (!name) {
      message = NAME_REQUIRED;
    } else if (name.length > 20) {
      message = NAME_MAX_LENGTH;
    } else if (GENERAL_TEXT_VALIDATION_PATTERN.test(name)) {
      message = NAME_INVALID_CHARS;
    }

    setErrorMessage(message);
    setIsErrorName(!!message); // messageが空文字列ならfalse、それ以外ならtrueを設定
  }, [name]);

  // 初期入力時のみ、バリデーションを実行しない
  useEffect(() => {
    if (isFirstInputName) {
      validateName();
    }
  }, [isFirstInputName, validateName]);

  return (
    <>
      <input
        type='text'
        placeholder='氏名（20文字以内）'
        value={name}
        onChange={(e) => {
          setName(e.target.value);
        }}
        onBlur={() => setIsFirstInputName(true)}
      />
      {isErrorName && <ErrorMessage message={errorMessage} />}
    </>
  );
};

export default SignUpName;
