import { useCallback, useEffect, useState } from 'react';
import ErrorMessage from './components/ErrorMessage';

const SignUp = () => {
  // 氏名の状態管理／入力フラグ／エラーフラグ
  const [name, setName] = useState(null);
  const [isFirstInputName, setIsFirstInputName] = useState(false);
  const [isErrorName, setIsErrorName] = useState(false);

  // メールアドレスの状態管理／入力フラグ／エラーフラグ
  const [email, setEmail] = useState(null);
  const [isFirstInputEmail, setIsFirstInputEmail] = useState(false);
  const [isErrorEmail, setIsErrorEmail] = useState(false);

  // パスワードの状態管理／入力フラグ／エラーフラグ
  const [pass, setPass] = useState(null);
  const [isRevealPassword, setIsRevealPassword] = useState(false);

  const [checkPass, setCheckPass] = useState(null);
  const [ruleCheck, setRuleCheck] = useState(null);
  const [isDisable, setIsDisable] = useState(false);

  // 氏名のバリデーションチェックを行う関数
  const validateName = useCallback(() => {
    const specialCharsPattern =
      /[^\wぁ-んァ-ン一-龥Ａ-Ｚａ-ｚ., -@!'"\#$%&\(\)*+,\-.\/:;<=>\?@\[\]\^_`\{\|\}~]/; // 特殊文字を含まない正規表現
    const isValidateName = !name || specialCharsPattern.test(name); // 入力が空文字もしくは特殊文字か

    setIsErrorName(isValidateName); // バリデーション結果に応じて真偽値を代入
  }, [name]);

  // 初回入力中のみ、氏名バリデーションを実行しない
  useEffect(() => {
    if (isFirstInputName) {
      validateName();
    }
  }, [isFirstInputName, validateName]);

  // メールアドレスのバリデーションチェックを行う関数
  const validateEmail = useCallback(() => {
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/; // メールアドレス形式の正規表現
    const isEmailValid = !emailPattern.test(email); // メールアドレス形式ではないのか

    setIsErrorEmail(isEmailValid); // バリデーション結果に応じて真偽値を代入
  }, [email]);

  // 初回入力中のみ、メールアドレスバリデーションを実行しない
  useEffect(() => {
    if (isFirstInputEmail) {
      validateEmail();
    }
  }, [isFirstInputEmail, validateEmail]);

  // パスワードのバリデーションチェックを行う関数
  const validatePassword = useCallback(() => {
    const hasUppercase = /[A-Z]/; // 半角大文字
    const hasLowercase = /[a-z]/; // 半角小文字
    const hasDigit = /\d/; //半角数値
    const hasSpecialChar = /[-!"#$%&'()*+,.\/:;<=>?@\[\]^_`{|}~]/; //許容した記号
  }, [pass]);

  return (
    <>
      <h1>新規登録</h1>
      <form onSubmit={''} className='signup-form'>
        <input
          type='text'
          placeholder='氏名（20文字以内）'
          value={name}
          onChange={(e) => {
            setName(e.target.value);
          }}
          onBlur={() => setIsFirstInputName(true)}
        />
        {isErrorName && <ErrorMessage message='文字を入力してください。' />}
        <input
          type='date'
          placeholder='生年月日'
          value={'dob'}
          onChange={(e) => {}}
        />
        <input
          type='email'
          placeholder='メールアドレス'
          value={email}
          onChange={(e) => {
            setEmail(e.target.value);
          }}
          onBlur={() => {
            setIsFirstInputEmail(true);
          }}
        />
        {isErrorEmail ? 'アウト' : undefined}
        <input
          type='password'
          placeholder='パスワード'
          value={pass}
          onChange={(e) => {
            setPass(e.target.value);
          }}
        />
        {'半角英数字'}
        <input
          type='password'
          placeholder='確認パスワード'
          value={''}
          onChange={(e) => {}}
        />
        <div className='checkbox-container'>
          <label>
            <input
              type='checkbox'
              checked={false}
              onChange={(e) => {}}
              className='input-checkbox'
            />
            規約同意
          </label>
        </div>
        <button type='submit' className='submit-button'>
          登録
        </button>
      </form>
    </>
  );
};

export default SignUp;
