import { useCallback, useEffect } from 'react';
import { GENERAL_TEXT_VALIDATION_PATTERN } from '../consts/const';

const SignUpName = ({ nameState, firstInputName }) => {
  const [name, setName] = nameState;
  const [isFirstInputName, setIsFirstInputName] = firstInputName;

  // 氏名のバリデーションチェックを行う関数
  const validateName = useCallback(() => {
    const isValidateName =
      !name || name.length > 20 || GENERAL_TEXT_VALIDATION_PATTERN.test(name); // 入力が0文字、21文字以上、特殊文字に該当するか

    setIsErrorName(isValidateName); // バリデーション結果に応じて真偽値を代入
  }, [name]);

  // 初回入力中のみ、氏名バリデーションを実行しない
  useEffect(() => {
    if (isFirstInputName) {
      validateName();
    }
  }, [isFirstInputName, validateName]);

  return (
    <>
      <input
        type='text'
        placeholder='氏名（20文字以内）'
        value={name}
        onChange={(e) => {
          setName(e.target.value);
        }}
        onBlur={() => setIsFirstInputName(true)}
      />
    </>
  );
};

export default SignUpName;
