import axios from 'axios';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { addUserData } from './store/modules/userState';

import {
  ENDPOINT_USER,
  INVALID_EMAIL_OR_PASSWORD_MESSAGE,
  NETWORK_ERROR,
} from './consts/const';
import ErrorMessage from './components/ErrorMessage';

const Login = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [loginId, setLoginId] = useState(''); // ログインID入力値の状態管理
  const [pass, setPass] = useState(''); // パスワード入力値の状態管理
  const [errorFlag, setErrorFlag] = useState(false); // エラーフラグの状態管理
  const [errorMessage, setErrorMessage] = useState(''); // エラーメッセージの状態管理

  // ログインIDやパスワードが未入力の場合、エラーとして早期リターン
  const verifyUser = () => {
    const isInputEmpty = (input) => input.trim() === '';

    if (isInputEmpty(loginId) || isInputEmpty(pass)) {
      displayError(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
      return;
    }
  };

  // ユーザ認証を行い、認証結果に応じて処理を行う関数
  const getAccount = async (e) => {
    e.preventDefault(); // フォームのデフォルト送信を防止

    // ログインIDやパスワードが未入力の場合、エラーとして早期リターン
    const isInputEmpty = (input) => input.trim() === '';
    if (isInputEmpty(loginId) || isInputEmpty(pass)) {
      displayError(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
      return;
    }

    try {
      const resData = await getCurrentUser(); // ユーザ情報を取得

      if (resData.length) {
        dispatch(addUserData(resData[0])); // レスポンスデータをstoreに格納
        navigate('/mypage'); // マイページに遷移
      } else {
        displayError(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
      }
    } catch (error) {
      displayError(error.message);
    }
  };

  // エラーを設定する関数
  const displayError = (message) => {
    setErrorFlag(true);
    setErrorMessage(message);
  };

  // ユーザ情報を取得する関数
  const getCurrentUser = async (e) => {
    try {
      const res = await axios.get(
        `${ENDPOINT_USER}?email=${loginId}&pass=${pass}`
      );
      return res.data;
    } catch (e) {
      dispatch(NETWORK_ERROR);
      throw new Error('ネットワークエラーが発生しました。');
    }
  };

  return (
    <>
      <h1>ログイン</h1>
      <form onSubmit={verifyUser} className='signup-form'>
        <input
          type='email'
          placeholder='ログインID（メールアドレス）'
          value={loginId}
          onChange={(e) => {
            setLoginId(e.target.value);
          }}
          className='input-field'
        />
        <input
          type='password'
          placeholder='パスワード'
          value={pass}
          onChange={(e) => {
            setPass(e.target.value);
          }}
          className='input-field'
        />
        <button type='submit' className='submit-button'>
          ログイン
        </button>
      </form>
      {errorFlag && <ErrorMessage message={errorMessage} />}
    </>
  );
};

export default Login;
