import { useCallback, useEffect, useState } from 'react';
import {
  UPPERCASE_REGEX,
  LOWERCASE_REGEX,
  DIGIT_REGEX,
  SPECIAL_CHAR_REGEX,
} from '../consts/const';
import ErrorMessage from './ErrorMessage';

const SignUpPass = ({ passState, setPassErrorFlag }) => {
  const [pass, setPass] = passState; // 入力値の状態管理（props）
  const setErrorFlag = setPassErrorFlag; // エラーフラグ_更新関数のみ（props）
  const [isFirstInputPass, setIsFirstInputPass] = useState(false); // 初期入力フラグ
  const [errorMessage, setErrorMessage] = useState(null); // エラーメッセージの状態管理

  const [hasUppercase, setHasUppercase] = useState(false); // 大文字が含まれているか状態管理
  const [hasLowercase, setHasLowercase] = useState(false); // 小文字が含まれているか状態管理
  const [hasDegit, setDegit] = useState(false); // 数字が含まれているか状態管理
  const [hasSpecialChar, setHasSpecialChar] = useState(false); // 記号が含まれているか状態管理

  // パスワードのバリデーションチェックを行う関数
  const validatePassword = useCallback(() => {
    const isUppercase = UPPERCASE_REGEX.test(pass); // 大文字が含まれているか
    isUppercase ? setHasUppercase(true) : setHasUppercase(false);

    const isLowercase = pass ? LOWERCASE_REGEX.test(pass) : false; // 小文字が含まれているか。nullを直接正規表現のtestメソッドに渡すと、nullが文字列"null"に変換され、その結果としてテストが実行される防止
    isLowercase ? setHasLowercase(true) : setHasLowercase(false);

    const isDegit = DIGIT_REGEX.test(pass);
  }, [pass]);

  // パスワードの入力条件を満たしているか確認
  useEffect(() => {
    validatePassword();
  }, [pass]);

  // 初期入力時のみ、バリデーションチェックを実行しない
  useEffect(() => {
    if (isFirstInputPass) {
    }
  }, [isFirstInputPass, validatePassword]);

  return (
    <>
      <input
        type='password'
        placeholder='パスワード'
        value={pass}
        onChange={(e) => {
          setPass(e.target.value);
        }}
        onBlur={() => setIsFirstInputPass(true)}
      />
      {errorMessage && (
        <ErrorMessage message='下記すべての条件を含めたパスワードを設定してください' />
      )}
      <ul className='list-container'>
        <li className={`uppercase-${hasUppercase}`}>半角大文字</li>
        <li className={`lowercase-${hasLowercase}`}>半角小文字</li>
        <li className={`degit-${hasDegit}`}>半角数値</li>
        <li className={`special-char-${hasSpecialChar}`}>半角記号</li>
      </ul>
    </>
  );
};

export default SignUpPass;
