import axios from 'axios';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  ENTPOINT_USER,
  INVALID_EMAIL_OR_PASSWORD_MESSAGE,
  NETWORK_ERROR,
} from './consts/const';
import ErrorMessage from './components/ErrorMessage';

const Login = () => {
  const navigate = useNavigate();

  const [loginId, setLoginId] = useState(''); // ログインID入力値の状態管理
  const [pass, setPass] = useState(''); // パスワード入力値の状態管理
  const [errorFlag, setErrorFlag] = useState(false); // エラーフラグの状態管理
  const [errorMessage, setErrorMessage] = useState(''); // エラーメッセージの状態管理

  // ユーザ情報の取得に応じて処理を行う関数
  const authenticateAndRoute = async (e) => {
    e.preventDefault(); // フォームのデフォルト送信を防止

    const resData = await getCurrentUser(); // ユーザ情報を取得
    console.log(resData);
    if (resData.length) {
      navigate('/mypage');
    } else {
      setErrorFlag(true);
      setErrorMessage(INVALID_EMAIL_OR_PASSWORD_MESSAGE);
    }
  };

  // ユーザ情報を取得する関数
  const getCurrentUser = async (e) => {
    try {
      const res = await axios.get(
        `${ENTPOINT_USER}?email=${loginId}&pass=${pass}`
      );
      console.log(res.success);
      return res.data;
    } catch (e) {
      setErrorFlag(true);
      setErrorMessage(NETWORK_ERROR);
      throw new Error('ネットワークエラーが発生しました。');
    }
  };

  return (
    <>
      <h1>ログイン</h1>
      <form onSubmit={authenticateAndRoute} className='signup-form'>
        <input
          type='email'
          placeholder='ログインID（メールアドレス）'
          value={loginId}
          onChange={(e) => {
            setLoginId(e.target.value);
          }}
          className='input-field'
        />
        <input
          type='password'
          placeholder='パスワード'
          value={pass}
          onChange={(e) => {
            setPass(e.target.value);
          }}
          className='input-field'
        />
        <button type='submit' className='submit-button'>
          ログイン
        </button>
      </form>
      {errorFlag && <ErrorMessage message={errorMessage} />}
    </>
  );
};

export default Login;
