import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import axios from 'axios';
import { ENDPOINT_BLOG, FETCH_BLOG_DATA_FAILURE } from './consts/const';
import { addBlogPosts as addPostsActions } from './store/modules/userState';
import BlogList from './components/BlogList';

const Mypage = () => {
  const navigate = useNavigate();

  // ダイレクトで遷移防止
  useEffect(() => {
    if (!localStorage.getItem('userToken')) {
      navigate('/');
    }
  }, []);

  const userState = useSelector((state) => state.user.userState);
  const blogPosts = useSelector((state) => state.user.blogPosts);
  const dispatch = useDispatch();

  console.log(userState);

  // userStateのIDをエンコードしてローカルストレージに格納（※今回は仮トークン扱い）
  if (userState.length !== 0) {
    const encodedUserId = btoa(userState.id);
    localStorage.setItem('userToken', encodedUserId);
  }

  // ローカルストレージから取得（※今回は仮トークン扱い）
  const storedToken = localStorage.getItem('userToken');
  const decodedUserId = atob(storedToken);

  // ユーザのブログデータを取得
  useEffect(() => {
    (async () => {
      try {
        const res = await axios.get(`${ENDPOINT_BLOG}?keyId=${decodedUserId}`);
        const posts = res.data[0].posts;
        dispatch(addPostsActions(posts));
      } catch (e) {
        console.error(FETCH_BLOG_DATA_FAILURE, e);
        navigate('/');
      }
    })();
  }, []);

  // ローカルストレージのデータを消去して、トップ画面に遷移する
  const logout = () => {
    localStorage.removeItem('userToken');
    navigate('/');
  };

  return (
    <>
      <h1>{userState.name}さんのページ</h1>
      <Link to='newPost' className='button new-post-button'>
        新規投稿
      </Link>
      <Link to='changepass' className='button change-pass-button'>
        パスワード変更
      </Link>
      {blogPosts.length ? (
        blogPosts.map((post) => <BlogList post={post} key={post.postDate} />)
      ) : (
        <div className='no-poslts-message'>投稿がありません</div>
      )}
      <button onClick={logout} className='button logout-button'>
        ログアウト
      </button>
    </>
  );
};

export default Mypage;
